var listOfNumbers = [1, 2, 3, 10, 100]

var firstIndex = 0
var lastIndex = listOfNumbers.count - 1

while firstIndex < lastIndex {
    // swap
    var tmp = listOfNumbers[firstIndex]
    listOfNumbers[firstIndex] = listOfNumbers[lastIndex]
    listOfNumbers[lastIndex] = tmp

    // go to next pair
    ++firstIndex
    --lastIndex
}

